import React, { useState } from 'react'
import { Link } from 'react-router-dom'

function NavBar() {
    const [darkMode, setDarkMode] = useState(false);

    const toggleMode = () => {
        setDarkMode(!darkMode);
        document.body.style.backgroundColor = darkMode ? "#f8f9fa" : "#302e2eff";
        document.body.style.color = darkMode ? "#000" : "#fff";
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-body-tertiary">
                <div className="container-fluid" style={{ borderBottom: '0.5px solid #0B5330' }}>
                    <img src="assets/images/logo.png" alt="logo" style={{ borderRadius: '13px', marginLeft: '30px' }}/>
                    <h1 className="navbar-brand" style={{ color: "#0B5330", marginRight: "200px", marginTop: "5px" }}>Recipes Finder</h1>
                    <div className="collapse navbar-collapse" id="navbarNav" style={{ marginLeft: '500px' }}>
                        <ul className="navbar-nav">
                    <li className="nav-item"><h5>
                        <Link style={{ color: darkMode ? "white" : "black" }} className="nav-link active" to="/">Home</Link></h5>
                    </li>
                    <li className="nav-item"><h5>
                        <Link style={{ color: darkMode ? "white" : "black" }} to="Favorites" className="nav-link">Favorites</Link></h5>
                    </li>
                        </ul>

            
                        <button onClick={toggleMode} style={{marginLeft: "20px",backgroundColor: darkMode ? "#0B5330" : "#e0e0e0",color: darkMode ? "white" : "black",border: "none",padding: "6px 15px",borderRadius: "20px",cursor: "pointer",fontWeight: "500",}}>{darkMode ? "Dark" : "Light"}
                        </button>
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default NavBar
