import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Details() {
  const { id } = useParams(); // نقرأ id من اللينك
  const [meal, setMeal] = useState(null);

  useEffect(() => {
    axios
      .get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`)
      .then((res) => {
        setMeal(res.data.meals[0]);
      });
  }, [id]);

  if (!meal) {
    return <h3 className="text-center mt-5">Loading...</h3>;
  }

  // نجهز المكونات + المقادير
  const ingredients = [];
  for (let i = 1; i <= 20; i++) {
    if (meal[`strIngredient${i}`]) {
      ingredients.push(
        `${meal[`strIngredient${i}`]} - ${meal[`strMeasure${i}`]}`
      );
    }
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">{meal.strMeal}</h2>
      <div className="row">
        {/* صورة */}
        <div className="col-md-6 text-center">
          <img
            src={meal.strMealThumb}
            alt={meal.strMeal}
            className="img-fluid rounded"
            style={{ maxHeight: "300px" }}
          />
          <h5 className="mt-3">Category: {meal.strCategory}</h5>
          <h6>Area: {meal.strArea}</h6>
        </div>

        {/* تفاصيل */}
        <div className="col-md-6">
          <h4>Instructions</h4>
          <p style={{ textAlign: "justify" }}>{meal.strInstructions}</p>

          <h4>Ingredients</h4>
          <ul>
            {ingredients.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      </div>

      {/* فيديو */}
      {meal.strYoutube && (
        <div className="mt-5 text-center">
          <h4>Recipe Video</h4>
          <iframe
            width="560"
            height="315"
            src={meal.strYoutube.replace("watch?v=", "embed/")}
            title={meal.strMeal}
            frameBorder="0"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
}

export default Details;
