import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Details() {
  const { id } = useParams();
  const [meal, setMeal] = useState(null);

  useEffect(() => {
    axios
      .get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`)
      .then((res) => {
        setMeal(res.data.meals[0]);
      });
  }, [id]);

  if (!meal) return <p className="text-center mt-5">Loading...</p>;

  return (
    <div className="container mt-5">
      <div
        className="card shadow-lg p-4"
        style={{ borderRadius: "20px", backgroundColor: "#f9f9f9" }}
      >
        {/* Meal Title */}
        <h2 className="text-center mb-3" style={{ color: "#0B5330" }}>
          {meal.strMeal}
        </h2>

        {/* Image */}
        <div className="text-center">
          <img
            src={meal.strMealThumb}
            alt={meal.strMeal}
            className="img-fluid mb-4"
            style={{
              borderRadius: "15px",
              maxWidth: "400px",
              boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
            }}
          />
        </div>

        {/* Category & Area */}
        <div className="text-center mb-4">
          <span
            className="badge bg-success fs-6 me-2"
            style={{ padding: "10px 15px", borderRadius: "12px" }}
          >
            {meal.strCategory}
          </span>
          <span
            className="badge bg-warning text-dark fs-6"
            style={{ padding: "10px 15px", borderRadius: "12px" }}
          >
            {meal.strArea}
          </span>
        </div>

        {/* Ingredients */}
        <h4 style={{ color: "#0B5330" }}>ðŸ›’ Ingredients</h4>
        <ul className="list-group mb-4">
          {Array.from({ length: 20 }).map((_, i) => {
            const ingredient = meal[`strIngredient${i + 1}`];
            const measure = meal[`strMeasure${i + 1}`];
            return ingredient ? (
              <li
                key={i}
                className="list-group-item"
                style={{
                  border: "none",
                  background: "transparent",
                  fontSize: "1rem",
                }}
              >
                âœ… {ingredient} - <b>{measure}</b>
              </li>
            ) : null;
          })}
        </ul>

        {/* Instructions */}
        <h4 style={{ color: "#0B5330" }}>ðŸ“– Instructions</h4>
        <p
          style={{
            lineHeight: "1.8",
            background: "#fff",
            padding: "15px",
            borderRadius: "10px",
            boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
          }}
        >
          {meal.strInstructions}
        </p>

        {/* Video */}
        {meal.strYoutube && (
          <div className="mt-4 text-center">
            <h4 style={{ color: "#0B5330" }}>ðŸŽ¥ Video Recipe</h4>
            <iframe
              width="560"
              height="315"
              src={meal.strYoutube.replace("watch?v=", "embed/")}
              title="YouTube video player"
              frameBorder="0"
              allowFullScreen
              style={{ borderRadius: "15px", marginTop: "15px" }}
            ></iframe>
          </div>
        )}
      </div>
    </div>
  );
}

export default Details;
