import React from 'react'
import axios from 'axios';
import { useEffect, useState } from 'react';

function Main() {

    const [text,setText]= useState([]);
    const [search,setSearch]= useState('');

    useEffect( ()=>{
        axios.get('https://www.themealdb.com/api/json/v1/1/search.php?s=Arrabiata')
        .then((Element)=>{ setText(Element.data.meals) })
    }, );

    const getSearch = (event)=>{
        setSearch(event.target.value);
    }

    
    return (
        <div>
            <div className="container" style={{marginTop:'50px'}}>
                <div className="row">

                    <div className='col-md-10 m-auto'>
                        <input className='form-control'placeholder='search with name' value={search} onChange={(e) => {
                            const value = e.target.value;
                        setSearch(value); }}/>
                    </div>

                    {text && text.length > 0 ? (
                    text.map((result) => (
                        <div key={result.idMeal} className="col-md-4 text-center mb-4">
                            <img src={result.strMealThumb} className="img-fluid" style={{ height: "100px" , width:"100px"}} alt={result.strMeal}/>
                            <h4>{result.strMeal}</h4>
                            <h6>{result.strCategory}</h6>
                            <h6>{result.strArea}</h6>
                            <h6>{result.idMeal}</h6>
                        </div>
                    ))
                    ) : (
                        <p className="text-center mt-4">null</p>
)}


                </div>
            </div>
        </div>
    )
}

export default Main
