import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Details() {
  const { id } = useParams();
  const [meal, setMeal] = useState(null);

  useEffect(() => {
    axios
      .get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`)
      .then((res) => {
        setMeal(res.data.meals[0]);
      });
  }, [id]);

  if (!meal) return <p className="text-center mt-5">Loading...</p>;

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">{meal.strMeal}</h2>
      <img
        src={meal.strMealThumb}
        alt={meal.strMeal}
        className="img-fluid d-block mx-auto mb-4"
        style={{ borderRadius: "15px", maxWidth: "400px" }}
      />
      <h4>Category: {meal.strCategory}</h4>
      <h5>Area: {meal.strArea}</h5>

      <h4 className="mt-4">Ingredients</h4>
      <ul>
        {Array.from({ length: 20 }).map((_, i) => {
          const ingredient = meal[`strIngredient${i + 1}`];
          const measure = meal[`strMeasure${i + 1}`];
          return ingredient ? (
            <li key={i}>
              {ingredient} - {measure}
            </li>
          ) : null;
        })}
      </ul>

      <h4 className="mt-4">Instructions</h4>
      <p>{meal.strInstructions}</p>

      {meal.strYoutube && (
        <div className="mt-4">
          <h4>Video Recipe</h4>
          <iframe
            width="560"
            height="315"
            src={meal.strYoutube.replace("watch?v=", "embed/")}
            title="YouTube video player"
            frameBorder="0"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
}

export default Details;
