import React from 'react'
import axios from 'axios';
import { useEffect, useState } from 'react';

function Main() {

    const [text,setText]= useState([]);
    const [search,setSearch]= useState('');

    useEffect( ()=>{
        axios.get('www.themealdb.com/api/json/v1/1/search.php?s=Arrabiata')
        .then((Element)=>{ setText(Element.data) })
    }, );

    const getSearch = (event)=>{
        setSearch(event.target.value);
    }

    
    return (
        <div>
            <div className="container" style={{marginTop:'50px'}}>
                <div className="row">

                    <div className='col-md-10 m-auto'>
                        <input className='form-control'placeholder='search with name' onChange={getSearch} />
                    </div>

                    {text.map( (result)=>{
                    return search==0 ?(
                        <div  key={result.id} className='col-md-4 text-center mb-4'>
                            <img src={result.images[0]} className='img-fluid' style={{height:'330px'}} alt='mm'></img>
                            <h4>{result.title}</h4>
                            <h6>{result.category}</h6>
                            <h6>{result.price}</h6>
                            <h6>{result.id}</h6>
                        </div>
                ): result.id==search ?(
                    <div  key={result.id} className='col-md-4 text-center mb-4'>
                            <img src={result.images[0]} className='img-fluid' style={{height:'330px'}} alt='mm'></img>
                            <h4>{result.title}</h4>
                            <h6>{result.category}</h6>
                            <h6>{result.price}</h6>
                            <h6>{result.id}</h6>
                        </div>
                ): null
                })
                }

                </div>
            </div>
        </div>
    )
}

export default Main
