import React from 'react'
import axios from 'axios';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import {FaHeart} from 'react-icons/fa';

function Main() {

    const [text,setText]= useState([]);
    const [search,setSearch]= useState('');

    const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem("favorites");
    return saved ? JSON.parse(saved) : [];
});

// دالة إضافة/إزالة الوصفة من الفيفوريت
const toggleFavorite = (meal) => {
    const isFav = favorites.some((fav) => fav.idMeal === meal.idMeal);
    let updatedFavs;
    if (isFav) {
        updatedFavs = favorites.filter((fav) => fav.idMeal !== meal.idMeal);
    } else {
        updatedFavs = [...favorites, meal];
    }
    setFavorites(updatedFavs);
    localStorage.setItem("favorites", JSON.stringify(updatedFavs));
};


    useEffect( ()=>{
    axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${search}`)
    .then((Element)=>{ setText(Element.data.meals) })
}, [search]);

    const getSearch = (event)=>{
        setSearch(event.target.value);
    }

    
    return (
        <div>
            <div className="container" style={{marginTop:'50px'}}>
                <div className="row">

                    <div className='col-md-10 m-auto'>
                        <input className='form-control'placeholder='search with name' value={search} onChange={(e) => {
                            const value = e.target.value;
                        setSearch(value); }}/>
                    </div>

                    {text && text.length > 0 ? (
                    text.map((result) => (
                        <div key={result.idMeal} className="col-md-4 text-center mb-4" style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", borderRadius:"10px", padding:"20px", marginTop:"30px"}}>
                            <img src={result.strMealThumb} className="img-fluid" style={{ height: "170px" , width:"170px"}} alt={result.strMeal}/>
                            <h4>{result.strMeal}</h4>
                            <h6>{result.strCategory}</h6>
                            <h6 style={{fontSize:"0.9rem"}}>{result.strArea}</h6>
                            <div>
                                <Link to="/"><button style={{borderRadius:"10px", color:"white", backgroundColor:"#0B5330", margin:"7px"}}>View recipe</button></Link>

                                <FaHeart onClick={() => toggleFavorite(result)} style={{ color: favorites.some((fav) => fav.idMeal === result.idMeal) ? "red" : "white", backgroundColor: "#0B5330", borderRadius: "50%", padding: "5px", cursor: "pointer", fontSize: "24px" }} />
                            </div>
                        </div>
                    ))
                    ) : (
                        <p className="text-center mt-4">null</p>
)}


                </div>
            </div>
        </div>
    )
}

export default Main
