import * as React from "react";
import { createRoot } from "react-dom/client";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import App from "./App";
import Fav from "./Fav";
import { FavoritesProvider } from "./Fav/FavoritesContext"; // استيراد الـ Provider

// تعريف الروتر
const router = createBrowserRouter([
  {
    path: "/",
    element: <App />,
  },
  {
    path: "Favorites",
    element: <Fav />, // مش محتاجة نبعت favorites كـ props دلوقتي
  },
]);


createRoot(document.getElementById("root")).render(
  <FavoritesProvider>
    <RouterProvider router={router} />
  </FavoritesProvider>
);
